
# Esta é uma função para somar dois numeros inteiros
def soma():
    x1 = int(input('Primeiro valor: '))
    x2 = int(input('Primeiro valor: '))
    print('O resultado de ', x1, '+', x2, ' é: ', (x1+x2))

# Esta é uma função para subtrair dois numeros inteiros
def subtracao():
    x1 = int(input('Primeiro valor: '))
    x2 = int(input('Primeiro valor: '))
    print('O resultado de ', x1, '-', x2, ' é: ', (x1-x2))


def main():
    soma()