#!/usr/bin/env python
from hashlib import sha256

import pytest

from pygcrypt.gctypes.key import Key
from pygcrypt.gctypes.sexpression import SExpression
from pygcrypt.utils import create_keys, randomize

@pytest.mark.skip(reason="Generating key takes time, so skip it.")
def test_generate_keys(context):
    (priv, pub) = create_keys()
    assert isinstance(priv, Key)
    assert isinstance(pub, Key)

def test_keylen(context, private, public):
    assert len(private) == 4096
    assert len(public) == 4096

def test_keygrip(context, private, public):
    assert private.keygrip == '1741a507591039059c5d565e4a041069c347a1b3'
    assert public.keygrip == '1741a507591039059c5d565e4a041069c347a1b3'

def test_sanity(context, private, public):
    assert private.issane() == True
    assert public.issane() == False

def test_algo(context, private, public):
    assert private.algo == u'rsa'
    assert public.algo == u'rsa'
    assert private.sign == True
    assert public.sign == True
    assert private.encr == True
    assert public.encr == True

def test_encryption(context, private, public):
    input_data = SExpression(b'(data (flags)(value %s))', b"Hello World!\n")
    encrypted_data = SExpression(b'(enc-val (rsa (a ##)))')
    decrypted_data = SExpression(b'(value %s)', b"Hello World!\n")
    assert public.encrypt(input_data) == encrypted_data
    assert private.decrypt(encrypted_data) == decrypted_data
    with pytest.raises(NotImplementedError):
        private.encrypt(input_data)
        public.decrypt(encrypted_data)

def test_sign(context, private, public):
    hashing = sha256()
    hashing.update(b"Test data inserted")
    data = hashing.digest()
    with pytest.raises(NotImplementedError):
        public.makesign(data)

    sig = private.makesign(data)
    assert sig == SExpression(b'(sig-val \n (rsa \n  (s ##)\n  )\n )\n')
    assert public.verify(sig, data) == True
    with pytest.raises(NotImplementedError):
        private.verify(sig, data)
